'use strict';

exports._accessLevel = function(mode){
	switch (mode.toLowerCase()) {
		case 'f': return this.fs.F_OK;
		case 'r': return this.fs.R_OK;
		case 'w': return this.fs.W_OK;
		case 'x': return this.fs.X_OK;
	}

	return 0;
};

exports.normalize = function(path){
	if (path && typeof path === 'string') {
		path = this.path.normalize(path);

		if (this.path.isAbsolute(path)) {
			return path;
		}

		return this.path.join(process.cwd(),path);
	}

	return process.cwd();
};